PRODUCTS_QUERY.query:
query Products($categoryName: String) {
  products(categoryName: $categoryName) {
    name
    in_stock
    images
    product_id
    price
    currency_label
    currency_symbol
    first_image
  }
}

PRODUCTDETAILS_QUERY.query:
query ProductDetails($productDetailsId: String!) {
  productDetails(id: $productDetailsId) {
    attributes {
      name
      values
    }
    currency_label
    currency_symbol
    description
    images
    name
    price
    in_stock
  }
}

